mapper.select:
		SELECT ROW_NUMBER()OVER ()ROWNUM
            , (SELECT CD_NM FROM CD WHERE CD_VALUE = PRJCT_STLE_CD) AS PRJCT_STLE_CD
            , p.PRJCT_ID
            , PRJCT_NM
            , (SELECT CD_NM FROM CD WHERE CD_VALUE = BIZ_STTS_CD) AS BIZ_STTS_CD_NM
            , BIZ_STTS_CD
            , (SELECT EMP_FLNM FROM EMP WHERE EMP_ID = PRJCT_MNGR_EMP_ID ) AS PRJCT_MNGR_EMP_ID
            , (SELECT CTMMNY_NM FROM CTMMNY_INFO ci WHERE ci.CTMMNY_ID = p.CTMMNY_ID) AS CTMMNY_NM
            , DATE_FORMAT(CTRT_YMD, '%Y-%m-%d') AS CTRT_YMD
            , DATE_FORMAT(BIZ_END_YMD, '%Y-%m-%d') AS BIZ_END_YMD
            , DATE_FORMAT(STBLE_END_YMD, '%Y-%m-%d') AS STBLE_END_YMD
            , COUNT(*) OVER () AS TOTAL_ITEMS
        FROM PRJCT p
        WHERE 1 = 1

	      AND PRJCT_STLE_CD = #{prjctStleCd}


	      AND PRJCT_ID = #{prjctId}


	      AND BIZ_FLFMT_TY_CD = #{bizFlfmtTyCd}


	      AND CTRT_YMD >= #{ctrtYmd}


	      AND BIZ_END_YMD <= #{bizEndYmd}



	      AND CTRT_YMD BETWEEN #{ctrtYmd} AND #{bizEndYmd}



          AND PRJCT_NM = #{prjctNm}


          AND CTMMNY_ID IN (SELECT CTMMNY_ID FROM CTMMNY_INFO WHERE CTMMNY_NM LIKE CONCAT('%',#{ctmmnyNm},'%'))

		ORDER BY p.REG_DT DESC

SELECT pma.EMP_ID, dept.DEPT_NM, e.EMP_FLNM, CONCAT(SUBSTR(#{startYmOdr},1, 6),'-',SUBSTR(#{startYmOdr}, 7, 1)) AS START_APLY_ODR, CONCAT(SUBSTR(#{endYmOdr},1, 6),'-',SUBSTR(#{endYmOdr}, 7, 1)) AS END_APLY_ODR, IFNULL(mmp.EXPECT_MM,0) AS EXPECT_MM, ROUND(SUM(CASE WHEN pmat.ATRZ_DMND_STTS_CD='VTW03703' THEN pma.MD ELSE 0 END/mm.COUNT),2) AS TOTAL_MD_SUM, ROUND((IFNULL(mmp.EXPECT_MM,0) - ROUND(SUM(CASE WHEN pmat.ATRZ_DMND_STTS_CD='VTW03703' THEN pma.MD ELSE 0 END/mm.COUNT),2)),2) AS USE_MM, SUM(pma.MD*8) AS CLM_MD FROM PRJCT_MM_APLY pma LEFT JOIN PRJCT_MM_ATRZ pmat ON pmat.PRJCT_ID = pma.PRJCT_ID AND (pmat.EMP_ID = pma.EMP_ID OR (pmat.EMP_ID IS NULL AND pma.EMP_ID IS NULL)) AND pmat.APLY_YM = pma.APLY_YM AND pmat.APLY_ODR = pma.APLY_ODR AND pmat.APLY_YMD = pma.APLY_YMD JOIN PRJCT_INDVDL_CT_MM picm ON pma.PRJCT_ID = picm.PRJCT_ID AND pma.EMP_ID = picm.EMP_ID AND pma.APLY_YM = picm.APLY_YM AND pma.APLY_ODR = picm.APLY_ODR AND picm.MM_ATRZ_CMPTN_YN = 'Y' JOIN EMP e ON e.EMP_ID = picm.EMP_ID JOIN ( SELECT DATE_FORMAT(CRTR_YMD, '%Y%m') AS YM, COUNT(*) AS COUNT FROM CRTR_DATE WHERE HLDY_CL_CD = 'VTW05001' GROUP BY DATE_FORMAT(CRTR_YMD, '%Y%m') ) AS mm ON (pma.APLY_YM = mm.YM) JOIN ( SELECT GROUP_CONCAT(DISTINCT d.DEPT_NM) AS DEPT_NM, pma.EMP_ID FROM DEPT d JOIN DEPT_HNF dh ON (d.DEPT_ID = dh.DEPT_ID) JOIN PRJCT_MM_APLY pma ON (pma.EMP_ID = dh.EMP_ID) GROUP BY pma.EMP_ID ) dept ON dept.EMP_ID = e.EMP_ID LEFT JOIN (SELECT mlp.PRJCT_ID, mlp.EMP_ID, mlp.MMNY_LBRCO_PRMPC_SN, mim.EXPECT_MM, MAX(mlp.BGT_MNG_ODR) FROM MMNY_LBRCO_PRMPC mlp JOIN MMNY_INPT_MM mim ON (mim.PRJCT_ID = mlp.PRJCT_ID AND mim.MMNY_LBRCO_PRMPC_SN = mlp.MMNY_LBRCO_PRMPC_SN AND mlp.BGT_MNG_ODR = mim.BGT_MNG_ODR) JOIN PRJCT_BGT_PRMPC pbp ON (mim.PRJCT_ID = pbp.PRJCT_ID AND mlp.BGT_MNG_ODR = pbp.BGT_MNG_ODR) WHERE 1=1 AND mim.PRJCT_ID = #{prjctId} AND mim.INPT_YM BETWEEN SUBSTR(#{startYmOdr},1, 6) AND SUBSTR(#{endYmOdr},1, 6) AND pbp.ATRZ_DMND_STTS_CD = 'VTW03703' GROUP BY mlp.EMP_ID, mlp.MMNY_LBRCO_PRMPC_SN, mim.EXPECT_MM) mmp ON (mmp.EMP_ID = pma.EMP_ID) WHERE 1 = 1
AND pma.PRJCT_ID = #{prjctId}
AND e.EMP_FLNM = #{empFlnm}
AND CONCAT(pma.APLY_YM, pma.APLY_ODR) BETWEEN #{startYmOdr} AND #{endYmOdr}
AND pma.EMP_ID = #{empId}
GROUP BY pma.EMP_ID, mmp.EXPECT_MM

SELECT (SELECT CD_NM FROM nwTr.CD cd WHERE UP_CD_VALUE = 'VTW045' AND CD_VALUE = pca.EXPENS_CD) AS EXPENS_NM, pca.EXPENS_CD, CONCAT(SUBSTR(#{startYmOdr},1, 6),'-',SUBSTR(#{startYmOdr}, 7, 1)) AS START_APLY_ODR, CONCAT(SUBSTR(#{endYmOdr},1, 6),'-',SUBSTR(#{endYmOdr}, 7, 1)) AS END_APLY_ODR, ( SELECT SUM(a.UTZTN_AMT) FROM PRJCT_CT_APLY a LEFT JOIN PRJCT_CT_ATRZ pcat ON (pcat.PRJCT_ID = a.PRJCT_ID AND pcat.EMP_ID = a.EMP_ID AND pcat.APLY_YM = a.APLY_YM AND pcat.APLY_ODR = a.APLY_ODR AND pcat.PRJCT_CT_APLY_SN = a.PRJCT_CT_APLY_SN) WHERE a.EXPENS_CD = pca.EXPENS_CD AND a.PRJCT_ID = pca.PRJCT_ID AND pcat.ATRZ_DMND_STTS_CD = 'VTW03703' GROUP BY a.EXPENS_CD ) AS TOTAL_AMT_SUM, SUM(pca.UTZTN_AMT) AS TOTAL_AMT FROM PRJCT_CT_APLY pca JOIN PRJCT_INDVDL_CT_MM picm ON pca.PRJCT_ID = picm.PRJCT_ID AND pca.EMP_ID = picm.EMP_ID AND pca.APLY_YM = picm.APLY_YM AND pca.APLY_ODR = picm.APLY_ODR WHERE 1 = 1
AND pca.PRJCT_ID = #{prjctId}
AND CONCAT(pca.APLY_YM, pca.APLY_ODR) BETWEEN #{startYmOdr} AND #{endYmOdr}
AND pca.EXPENS_CD = #{expensCd}
GROUP BY TOTAL_AMT_SUM, EXPENS_CD, START_APLY_ODR, END_APLY_ODR

SELECT pma.MD*8 AS MD, CONCAT(pma.MD*8, 'hrs | ', e.EMP_FLNM, '(', c.CD_NM, ')') AS TEXT, c.CD_NM AS ATRZ_DMND_STTS_NM , DATE_FORMAT(pma.APLY_YMD, '%Y-%m-%d') AS START_DATE, DATE_FORMAT(pma.APLY_YMD,'%Y-%m-%d') AS END_DATE, (SELECT EMP_FLNM FROM EMP WHERE EMP_ID = pah.APRVR_EMP_ID) AS APRVR_EMP_FLNM, dept.DEPT_NM FROM PRJCT_MM_APLY pma JOIN PRJCT_MM_ATRZ pah ON pma.PRJCT_ID = pah.PRJCT_ID AND pma.EMP_ID = pah.EMP_ID AND pma.APLY_YM = pah.APLY_YM AND pma.APLY_YMD = pah.APLY_YMD AND pma.APLY_ODR = pah.APLY_ODR AND pah.ATRZ_DMND_STTS_CD = 'VTW03703' -- 결재요청상태코드 : 승인 JOIN EMP e ON pma.EMP_ID = e.EMP_ID JOIN CD c ON pah.ATRZ_DMND_STTS_CD = c.CD_VALUE JOIN ( SELECT GROUP_CONCAT(DISTINCT d.DEPT_NM) AS DEPT_NM, pma.EMP_ID FROM DEPT d JOIN DEPT_HNF dh ON (d.DEPT_ID = dh.DEPT_ID) JOIN PRJCT_MM_APLY pma ON (pma.EMP_ID = dh.EMP_ID) GROUP BY pma.EMP_ID ) dept ON dept.EMP_ID = e.EMP_ID WHERE 1 = 1
AND pma.PRJCT_ID = #{prjctId}
AND CONCAT(pma.APLY_YM, pma.APLY_ODR) BETWEEN #{startYmOdr} AND #{endYmOdr}
AND pma.EMP_ID = #{empId}

SELECT PIVOT_DATE, COALESCE(APLY_YMD, '') AS APLY_YMD, COALESCE(PRJCT_NM, 'null_project') AS PRJCT_NM, COALESCE(EMP_FLNM, 'null_flnm') AS EMP_FLNM, COALESCE(EMP_DETAIL, 'null_detail') AS EMP_DETAIL, COALESCE(CD_NM, 'null_cd') AS CD_NM, COALESCE(UTZTN_AMT, '') AS UTZTN_AMT FROM ( WITH RECURSIVE a AS ( SELECT CAST(CONCAT(SUBSTR(#{startYmOdr},1, 6), '01') AS DATE) AS PIVOT_DATE UNION ALL SELECT DATE_ADD(PIVOT_DATE, INTERVAL 1 DAY) FROM a WHERE DATE_ADD(PIVOT_DATE, INTERVAL 1 DAY) < CAST(CONCAT(SUBSTR(#{endYmOdr}, 1, 6), '01') AS DATE) + INTERVAL 1 MONTH ), b AS ( SELECT CONCAT(SUBSTR(b.UTZTN_DT, 1, 4),'-',SUBSTR(b.UTZTN_DT, 5, 2),'-',SUBSTR(b.UTZTN_DT, 7, 2)) AS APLY_YMD, p.PRJCT_NM, (SELECT EMP_FLNM FROM nwTr.EMP emp WHERE EMP_ID = b.EMP_ID) AS EMP_FLNM, CONCAT('상세내역 : ', b.CT_PRPOS, ' | 용도 : ', b.ATDRN) AS EMP_DETAIL, null AS MD, (SELECT CD_NM FROM nwTr.CD cd WHERE UP_CD_VALUE = 'VTW045' AND CD_VALUE = b.EXPENS_CD) AS CD_NM, b.UTZTN_AMT, b.USE_OFFIC, b.CT_PRPOS, b.ATDRN FROM nwTr.PRJCT_INDVDL_CT_MM a, nwTr.PRJCT_CT_APLY b JOIN PRJCT p ON b.PRJCT_ID = p.PRJCT_ID WHERE 1 = 1 AND a.PRJCT_ID = b.PRJCT_ID AND a.APLY_YM = b.APLY_YM AND a.APLY_ODR = b.APLY_ODR AND a.EMP_ID = b.EMP_ID AND a.CT_ATRZ_CMPTN_YN = 'Y'
AND a.PRJCT_ID = #{prjctId}
AND CONCAT(a.APLY_YM, a.APLY_ODR) BETWEEN #{startYmOdr} AND #{endYmOdr}
AND b.EXPENS_CD = #{expensCd}
) SELECT * FROM ( SELECT * FROM a WHERE ( (SUBSTR(#{startYmOdr},7, 1) = 1 AND PIVOT_DATE >= CONCAT(SUBSTR(#{startYmOdr},1, 6), '01')) OR (SUBSTR(#{startYmOdr},7, 1) = 2 AND PIVOT_DATE >= CONCAT(SUBSTR(#{startYmOdr},1, 6), '16')) ) AND ( (SUBSTR(#{endYmOdr},7, 1) = 1 AND PIVOT_DATE <= CONCAT(SUBSTR(#{endYmOdr},1, 6), '15')) OR (CONCAT(SUBSTR(#{endYmOdr}, 7, 1) = 2 AND PIVOT_DATE <= LAST_DAY(CAST(CONCAT(SUBSTR(#{endYmOdr}, 1, 6), '01') AS DATE)))) ) ) c LEFT JOIN b ON c.PIVOT_DATE = b.APLY_YMD ) a ORDER BY PIVOT_DATE

SELECT DISTINCT(EMP_ID) AS EMP_ID
FROM PRJCT_INDVDL_CT_MM
WHERE PRJCT_ID = #{prjctId}

WITH RECURSIVE c AS ( SELECT CAST(CONCAT(SUBSTR(#{startYmOdr}, 1, 6), '01') AS DATE) AS PIVOT_DATE UNION ALL SELECT DATE_ADD(PIVOT_DATE, INTERVAL 1 DAY) FROM c WHERE DATE_ADD(PIVOT_DATE, INTERVAL 1 DAY) <= IF( RIGHT(#{startYmOdr}, 1) = '1', DATE(CONCAT(SUBSTR(#{startYmOdr}, 1, 6), '15')), LAST_DAY(CAST(CONCAT(SUBSTR(#{endYmOdr}, 1, 6), '01') AS DATE)) ) ) SELECT PIVOT_DATE , (SELECT PRJCT_NM FROM PRJCT P WHERE PRJCT_ID = #{prjctId}) AS PRJCT_NM , (SELECT EMP_FLNM FROM EMP EP WHERE EP.EMP_ID = #{empId}) AS EMP_FLNM , EXPENS_CD , UTZTN_DT , IFNULL(SUM(UTZTN_AMT),0) AS UTZTN_AMT , IFNULL((MD * 8),0) AS MD , APLY_ODR FROM (SELECT DATE_FORMAT(PIVOT_DATE,'%Y%m%d') AS PIVOT_DATE FROM c WHERE PIVOT_DATE <= IF( RIGHT('20240501', 1) = '1', DATE(CONCAT(SUBSTR('20240501', 1, 6), '15')), LAST_DAY(CAST(CONCAT(SUBSTR('20240501', 1, 6), '01') AS DATE)))) T LEFT JOIN ( SELECT P.PRJCT_NM, E.EMP_FLNM, (SELECT CD_NM FROM CD C WHERE C.CD_VALUE = PA.EXPENS_CD) AS EXPENS_CD, DATE_FORMAT(PA.UTZTN_DT, '%Y%m%d') AS UTZTN_DT, PA.UTZTN_AMT, PM.MD, PA.APLY_ODR FROM PRJCT_CT_APLY PA LEFT JOIN PRJCT P ON PA.PRJCT_ID = P.PRJCT_ID LEFT JOIN EMP E ON E.EMP_ID = PA.EMP_ID LEFT JOIN PRJCT_MM_APLY PM ON PA.PRJCT_ID = PM.PRJCT_ID AND DATE_FORMAT(PA.UTZTN_DT, '%Y%m%d') = PM.APLY_YMD AND PA.EMP_ID = PM.EMP_ID WHERE P.PRJCT_ID = #{prjctId}
AND E.EMP_ID = #{empId}
UNION ALL SELECT P.PRJCT_NM, E.EMP_FLNM, (SELECT CD_NM FROM CD C WHERE C.CD_VALUE = PA.EXPENS_CD) AS EXPENS_CD, DATE_FORMAT(PM.APLY_YMD, '%Y%m%d') AS APLY_YMD, NULL AS UTZTN_AMT, PM.MD, NULL AS APLY_ODR FROM PRJCT_MM_APLY PM LEFT JOIN PRJCT P ON PM.PRJCT_ID = P.PRJCT_ID LEFT JOIN EMP E ON E.EMP_ID = PM.EMP_ID LEFT JOIN PRJCT_CT_APLY PA ON PM.PRJCT_ID = PA.PRJCT_ID AND DATE_FORMAT(PM.APLY_YMD, '%Y%m%d') = DATE_FORMAT(PA.UTZTN_DT, '%Y%m%d') AND PM.EMP_ID = PA.EMP_ID WHERE PA.PRJCT_ID IS NULL AND P.PRJCT_ID = #{prjctId}
AND E.EMP_ID = #{empId}
) Z ON T.PIVOT_DATE = UTZTN_DT GROUP BY PIVOT_DATE, PRJCT_NM, EMP_FLNM, EXPENS_CD, UTZTN_DT, MD, APLY_ODR

SELECT pca.ELCTRN_ATRZ_ID AS unique_id, CONCAT(p.PRJCT_NM, ' (PM : ', (SELECT EMP_FLNM FROM EMP WHERE EMP_ID = p.PRJCT_MNGR_EMP_ID), ')') AS PRJCT_NM, -- 프로젝트명 (SELECT CD_NM FROM CD WHERE CD_VALUE = pca.EXPENS_CD) AS EXPENS_CD, -- 비용 코드 e.EMP_FLNM as EMP_FLNM, CONCAT('성명 : ', e.EMP_FLNM, CHAR(10), ' | 상세내역 : ', pca.CT_PRPOS, ' | 용도 : ', pca.ATDRN) AS PRJCT_DETAIL, CONCAT('상세내역 : ', pca.CT_PRPOS, ' | 용도 : ', pca.ATDRN) AS EMP_DETAIL, pca.UTZTN_AMT as UTZTN_AMT, CONCAT(SUBSTR(pca.UTZTN_DT, 1, 4), '-', SUBSTR(pca.UTZTN_DT, 5, 2), '-', SUBSTR(pca.UTZTN_DT, 7, 2)) AS UTZTN_DT FROM PRJCT_CT_APLY pca JOIN PRJCT_CT_ATRZ pah ON pca.PRJCT_CT_APLY_SN = pah.PRJCT_CT_APLY_SN AND pca.EMP_ID = pah.EMP_ID AND pca.PRJCT_ID = pah.PRJCT_ID AND pca.APLY_YM = pah.APLY_YM AND pca.APLY_ODR = pah.APLY_ODR AND pah.ATRZ_DMND_STTS_CD = 'VTW03703' -- 결재요청상태코드 : 승인 JOIN EMP e ON pca.EMP_ID = e.EMP_ID -- JOIN PRJCT p ON pca.PRJCT_ID = p.PRJCT_ID LEFT JOIN ELCTRN_ATRZ ea ON pca.ELCTRN_ATRZ_ID = ea.ELCTRN_ATRZ_ID LEFT JOIN CLM_ATRZ ca ON ea.ELCTRN_ATRZ_ID = ca.ELCTRN_ATRZ_ID LEFT JOIN ELCTRN_ATRZ_DOC_FORM eadf ON ea.ATRZ_FORM_DOC_ID = eadf.ATRZ_FORM_DOC_ID WHERE 1 = 1
AND pca.PRJCT_ID = #{prjctId}
AND pca.APLY_YM = #{aplyYm}
AND pca.APLY_ODR = #{aplyOdr}
AND e.EMPNO = #{empNo}
AND pca.EXPENS_CD = #{expensCd}
-- ) b -- ON a.PIVOT_DATE = b.UTZTN_DT -- ORDER BY PIVOT_DATE

SELECT ALL_VTW, MM_03701, (ALL_VTW - MM_03701) AS MM_03701_MIN, MM_03702, (MM_03701 - MM_03702) AS MM_03702_MIN, MM_037034, (MM_03702 - MM_037034) AS MM_037034_MIN, CT_03701, (ALL_VTW - CT_03701) AS CT_03701_MIN, CT_03702, (CT_03701 - CT_03702) AS CT_03702_MIN, CT_037034, (CT_03702 - CT_037034) AS CT_037034_MIN, NULL AS EMP_ID FROM ( SELECT COUNT(1) AS ALL_VTW FROM EMP e WHERE 1 = 1
AND HDOF_STTS_CD = #{hdofSttsCd}
AND EMP_ID = #{empId}
AND EMP_TY_CD != 'VTW00203' AND e.EMPNO NOT IN ('VK0101', 'VK0102', 'VK0103', 'VK0104', 'VK0105', 'VK0106', 'VK0107', 'VK0108', 'VK0109', 'VK0110', 'VM1000') ) a, ( SELECT COUNT(DISTINCT CASE WHEN pah.ATRZ_DMND_STTS_CD IN ('','VTW03701','VTW03702','VTW03703','VTW03704') THEN pma.EMP_ID END) AS MM_03701, COUNT(DISTINCT CASE WHEN pah.ATRZ_DMND_STTS_CD IN ('VTW03702','VTW03703','VTW03704') THEN pma.EMP_ID END) AS MM_03702, COUNT(DISTINCT CASE WHEN pah.ATRZ_DMND_STTS_CD IN ('VTW03703','VTW03704') THEN pma.EMP_ID END) AS MM_037034 FROM PRJCT_MM_APLY pma JOIN PRJCT_MM_ATRZ pah ON pma.PRJCT_ID = pah.PRJCT_ID AND pma.EMP_ID = pah.EMP_ID AND pma.APLY_YM = pah.APLY_YM AND pma.APLY_ODR = pah.APLY_ODR -- AND pah.PRJCT_ATRZ_HIST_SN = ( -- SELECT MAX(PRJCT_ATRZ_HIST_SN) -- FROM PRJCT_ATRZ_HIST a -- WHERE a.PRJCT_ID = pah.PRJCT_ID -- AND a.EMP_ID = pah.EMP_ID -- AND a.APLY_YM = pah.APLY_YM -- AND a.APLY_ODR = pah.APLY_ODR -- ) WHERE 1 = 1
AND pma.APLY_YM = #{aplyYm}
AND pma.APLY_ODR = #{aplyOdr}
AND pma.EMP_ID IN (SELECT EMP_ID FROM EMP WHERE HDOF_STTS_CD = #{hdofSttsCd})
AND pma.EMP_ID = #{empId}
) b, ( SELECT COUNT(DISTINCT CASE WHEN pah.ATRZ_DMND_STTS_CD IN ('','VTW03701','VTW03702','VTW03703','VTW03704') THEN pca.EMP_ID END) AS CT_03701, COUNT(DISTINCT CASE WHEN pah.ATRZ_DMND_STTS_CD IN ('VTW03702','VTW03703','VTW03704') THEN pca.EMP_ID END) AS CT_03702, COUNT(DISTINCT CASE WHEN pah.ATRZ_DMND_STTS_CD IN ('VTW03703','VTW03704') THEN pca.EMP_ID END) AS CT_037034 FROM PRJCT_CT_APLY pca JOIN PRJCT_CT_ATRZ pah ON pca.PRJCT_ID = pah.PRJCT_ID AND pca.EMP_ID = pah.EMP_ID AND pca.APLY_YM = pah.APLY_YM AND pca.APLY_ODR = pah.APLY_ODR
AND pca.PRJCT_CT_APLY_SN = pah.PRJCT_CT_APLY_SN WHERE 1 = 1
AND pca.APLY_YM = #{aplyYm}
AND pca.APLY_ODR = #{aplyOdr}
AND pca.EMP_ID IN (SELECT EMP_ID FROM EMP WHERE HDOF_STTS_CD = #{hdofSttsCd})
AND pca.EMP_ID = #{empId}
) c

SELECT DISTINCT Z.CRTR_YMD AS APLY_YMD, M.PRJCT_ID AS PRJCT_ID, M.PRJCT_NM AS PRJCT_NM, M.EMP_FLNM AS EMP_FLNM, IFNULL(T.MD, 0) AS MD FROM ( SELECT DATE_FORMAT(cd.CRTR_YMD, '%Y-%m-%d') AS CRTR_YMD FROM CRTR_DATE cd WHERE cd.CRTR_YMD LIKE '202405%' AND cd.CRTR_ODR = '1' ) Z JOIN ( SELECT DISTINCT P.PRJCT_ID, P.PRJCT_NM, E.EMP_FLNM FROM PRJCT P JOIN PRJCT_MM_APLY PMA ON P.PRJCT_ID = PMA.PRJCT_ID JOIN PRJCT_INDVDL_CT_MM PICM ON PMA.PRJCT_ID = PICM.PRJCT_ID AND PMA.EMP_ID = PICM.EMP_ID AND PMA.APLY_YM = PICM.APLY_YM AND PMA.APLY_ODR = PICM.APLY_ODR JOIN EMP E ON PMA.EMP_ID = E.EMP_ID WHERE PICM.MM_ATRZ_CMPTN_YN = 'Y'
AND DATE_FORMAT(PMA.APLY_YMD , '%Y-%m-%d') = #{aplyYmd}
AND PMA.APLY_ODR = #{aplyOdr}
AND P.PRJCT_ID = #{prjctId}
) M ON 1=1 LEFT JOIN ( SELECT P.PRJCT_ID AS PRJCT_ID, DATE_FORMAT(PMA.APLY_YMD , '%Y-%m-%d') AS APLY_YMD, P.PRJCT_NM AS PRJCT_NM, E.EMP_FLNM AS EMP_FLNM, CASE WHEN PMA.MD = 1 THEN 8 WHEN PMA.MD = 0.5 THEN 4 ELSE 0 END AS MD FROM PRJCT_MM_APLY PMA JOIN PRJCT P ON P.PRJCT_ID = PMA.PRJCT_ID JOIN PRJCT_INDVDL_CT_MM PICM ON PMA.PRJCT_ID = PICM.PRJCT_ID AND PMA.EMP_ID = PICM.EMP_ID AND PMA.APLY_YM = PICM.APLY_YM AND PMA.APLY_ODR = PICM.APLY_ODR JOIN EMP E ON PMA.EMP_ID = E.EMP_ID WHERE PICM.MM_ATRZ_CMPTN_YN = 'Y'
AND DATE_FORMAT(PMA.APLY_YMD , '%Y-%m-%d') = #{aplyYmd}
AND PMA.APLY_ODR = #{aplyOdr}
AND P.PRJCT_ID = #{prjctId}
) T ON T.APLY_YMD = Z.CRTR_YMD AND M.PRJCT_ID = T.PRJCT_ID ORDER BY M.EMP_FLNM, APLY_YMD;

	SELECT pca.PRJCT_CT_APLY_SN, pca.PRJCT_ID
	  FROM PRJCT_CT_APLY pca
	  JOIN PRJCT_CT_ATRZ pcat ON (pcat.PRJCT_CT_APLY_SN = pca.PRJCT_CT_APLY_SN AND pcat.PRJCT_CT_APLY_SN = pca.PRJCT_CT_APLY_SN AND pca.PRJCT_ID = pcat.PRJCT_ID AND pca.EMP_ID = pcat.EMP_ID AND pca.APLY_YM = pcat.APLY_YM)
	 WHERE 1=1
      AND (pca.CT_ATRZ_SE_CD = 'VTW01902' OR pca.CT_ATRZ_SE_CD = 'VTW01903')
	   AND pca.EMP_ID = #{empId}
	   AND pca.APLY_YM = #{aplyYm}
	   AND pca.APLY_ODR = #{aplyOdr}
	   AND pcat.ATRZ_DMND_STTS_CD = 'VTW03703'

 SELECT DISTINCT (DDLN_YN) AS DDLN_YN
 FROM CRTR_DATE cd
WHERE 1=1
  AND SUBSTR(CRTR_YMD, 1, 6) = #{aplyYm}
  AND CRTR_ODR = #{aplyOdr}

UPDATE CRTR_DATE
