/* financialAffairMngMapper.updateClturPhstrnActctReg */
UPDATE CLTUR_PHSTRN_ACT_CT_REG
SET
    CLTUR_PHSTRN_SE_CD = #{clturPhstrnSeCd}
    ,CLM_AMT = #{clmAmt}
    ,RM = #{rm}
    ,REG_DT = SYSDATE()
    ,REG_EMP_ID = #{sessionEmpId}
WHERE EMP_ID = #{empId}
AND CLTUR_PHSTRN_ACT_CT_SN = #{clturPhstrnActCtSn}
