mapper.select:
SELECT (SELECT CD_NM FROM CD WHERE CD_VALUE = va.VCATN_TY_CD) AS VCATN_TY_CD -- 휴가구분 ,(SELECT EMPNO FROM EMP WHERE EMP_ID IN (SELECT ATRZ_DMND_EMP_ID FROM ELCTRN_ATRZ ea WHERE ea.ELCTRN_ATRZ_ID = va.ELCTRN_ATRZ_ID ) ) as EMPNO -- 사원번호 ,(SELECT EMP_FLNM FROM EMP WHERE EMP_ID IN (SELECT ATRZ_DMND_EMP_ID FROM ELCTRN_ATRZ ea WHERE ea.ELCTRN_ATRZ_ID = va.ELCTRN_ATRZ_ID ) ) as EMP_FLNM -- 사원명 , STR_TO_DATE(VCATN_BGNG_YMD, '%Y%m%d') VCATN_BGNG_YMD -- 시작일 , STR_TO_DATE(VCATN_END_YMD, '%Y%m%d') VCATN_END_YMD -- 종료일 , VCATN_DE_CNT -- 휴가일수 , VCATN_PRVONSH -- 휴가사유 , COUNT(*) OVER () AS TOTAL_ITEMS FROM VCATN_ATRZ va WHERE 1=1
AND VCATN_BGNG_YMD >= #{vcatnBgngYmd}
AND VCATN_END_YMD <= #{vcatnEndYmd}
AND VCATN_BGNG_YMD BETWEEN #{vcatnBgngYmd} AND #{vcatnEndYmd}
HAVING EMPNO = #{empno}

WITH VCANT_LIST AS( SELECT EMP.EMP_ID /* 사원ID */ ,EMP.EMPNO /* 사원번호 */ ,EMP.EMP_FLNM /* 사원명 */ ,(SELECT CD_VALUE FROM CD WHERE EMP.JBPS_CD = CD.CD_VALUE) JOB_CD /* 직급코드 */ ,(SELECT CD_NM FROM CD WHERE EMP.JBPS_CD = CD.CD_VALUE) JOB_NM /* 직급명 */ ,DEPT.DEPT_ID /* 부서ID */ ,DEPT.DEPT_NM /* 부서명 */ ,(SELECT CD_VALUE FROM CD WHERE EMP.HDOF_STTS_CD = CD.CD_VALUE) HDOF_STTS_CD /* 재직상태코드 */ ,(SELECT CD_NM FROM CD WHERE EMP.HDOF_STTS_CD = CD.CD_VALUE) HDOF_STTS_NM /* 재직상태명 */ ,VMNG.VCATN_ALTMNT_SN /* 휴가배정순번 */ ,VMNG.VCATN_YR /* 휴가연도 */ ,VMNG.VCATN_ALTMNT_DAYCNT /* 휴가배정일수 */ ,VMNG.USE_DAYCNT /* 사용일수 */ ,VMNG.VCATN_REMNDR_DAYCNT /* 휴가잔여일수 */ ,VMNG.NEW_VCATN_ALTMNT_DAYCNT /* 신규휴가배정일수 */ ,VMNG.NEW_USE_DAYCNT /* 신규사용일수 */ ,VMNG.NEW_REMNDR_DAYCNT /* 신규잔여일수 */ ,VMNG.ALTMNT_BGNG_YMD /* 배정시작일자 */ ,VMNG.ALTMNT_USE_END_YMD /* 배정사용종료일자 */ ,IFNULL(VMNG.NEW_VCATN_YN, 'N') NEW_VCATN_YN /* 신규휴가여부 */ ,CASE WHEN DATEDIFF(DATE_FORMAT(NOW(), '%Y%m%d'), DATE_FORMAT(CONCAT(DATE_FORMAT(NOW(), '%Y'), '0401'), '%Y%m%d')) < 0 THEN DATE_FORMAT(NOW(), '%Y') - 1 ELSE DATE_FORMAT(NOW(), '%Y') END FLAG_YEAR /* 회계년도 */ ,CASE WHEN NEW_VCATN_YN = 'Y' THEN NEW_VCATN_ALTMNT_DAYCNT ELSE VCATN_ALTMNT_DAYCNT END TABLE_ALTMNT_DAYCNT ,CASE WHEN NEW_VCATN_YN = 'Y' THEN NEW_USE_DAYCNT ELSE USE_DAYCNT END TABLE_USE_DAYCNT ,CASE WHEN NEW_VCATN_YN = 'Y' THEN NEW_REMNDR_DAYCNT ELSE VCATN_REMNDR_DAYCNT END TABLE_REMNDR_DAYCNT FROM VCATN_MNG VMNG RIGHT JOIN EMP EMP ON EMP.EMP_ID = VMNG.EMP_ID LEFT JOIN DEPT_HNF HNF ON HNF.EMPNO = EMP.EMPNO LEFT JOIN DEPT DEPT ON DEPT.DEPT_ID = HNF.DEPT_ID WHERE 1 = 1
AND DEPT.DEPT_ID = #{deptId}
) SELECT DISTINCT VCATN.EMP_ID /* 사원ID */ ,VCATN.EMPNO /* 사원번호 */ ,VCATN.EMP_FLNM /* 사원명 */ ,VCATN.JOB_CD /* 직급코드 */ ,VCATN.JOB_NM /* 직급명 */ ,VCATN.HDOF_STTS_NM /* 재직상태명 */ ,VCATN.VCATN_ALTMNT_SN /* 휴가배정순번 */ ,VCATN.VCATN_YR /* 휴가연도 */ ,VCATN.VCATN_ALTMNT_DAYCNT /* 휴가배정일수 */ ,VCATN.USE_DAYCNT /* 사용일수 */ ,VCATN.VCATN_REMNDR_DAYCNT /* 휴가잔여일수 */ ,VCATN.NEW_VCATN_ALTMNT_DAYCNT /* 신규휴가배정일수 */ ,VCATN.NEW_USE_DAYCNT /* 신규사용일수 */ ,VCATN.NEW_REMNDR_DAYCNT /* 신규잔여일수 */ ,VCATN.ALTMNT_BGNG_YMD /* 배정시작일자 */ ,VCATN.ALTMNT_USE_END_YMD /* 배정사용종료일자 */ ,VCATN.NEW_VCATN_YN /* 신규휴가여부 */ ,VCATN.TABLE_ALTMNT_DAYCNT /* 배정일수 */ ,VCATN.TABLE_USE_DAYCNT /* 사용일수 */ ,VCATN.TABLE_REMNDR_DAYCNT /* 잔여일수 */ FROM VCANT_LIST VCATN WHERE 1 = 1
AND VCATN_YR = FLAG_YEAR
AND VCATN_YR = #{vcatnYr}
AND EMP_ID = #{empId}
AND NEW_VCATN_YN = #{newVcatnYn}
AND EMPNO LIKE CONCAT('%', #{empno}, '%')
AND EMP_FLNM LIKE CONCAT('%', #{empFlnm}, '%')
AND JOB_CD = #{jobCd}
AND HDOF_STTS_CD = #{hdofSttsCd}
ORDER BY JOB_CD

SELECT * FROM ( SELECT EMP.EMP_ID /* 사원ID */ ,EMP.EMPNO /* 사원번호 */ ,EMP.EMP_FLNM /* 사원명 */ ,(SELECT CD_VALUE FROM CD WHERE EMP.JBPS_CD = CD.CD_VALUE) JOB_CD /* 직급코드 */ ,(SELECT CD_NM FROM CD WHERE EMP.JBPS_CD = CD.CD_VALUE) JOB_NM /* 직급명 */ ,DEPT.DEPT_ID /* 부서ID */ ,DEPT.DEPT_NM /* 부서명 */ ,(SELECT CD_VALUE FROM CD WHERE EMP.HDOF_STTS_CD = CD.CD_VALUE) HDOF_STTS_CD /* 재직상태코드 */ ,(SELECT CD_NM FROM CD WHERE EMP.HDOF_STTS_CD = CD.CD_VALUE) HDOF_STTS_NM /* 재직상태명 */ ,VMNG.VCATN_ALTMNT_SN /* 휴가배정순번 */ ,VMNG.VCATN_YR /* 휴가연도 */ ,VMNG.VCATN_ALTMNT_DAYCNT /* 휴가배정일수 */ ,VMNG.USE_DAYCNT /* 사용일수 */ ,VMNG.VCATN_REMNDR_DAYCNT /* 휴가잔여일수 */ ,VMNG.NEW_VCATN_ALTMNT_DAYCNT /* 신규휴가배정일수 */ ,VMNG.NEW_USE_DAYCNT /* 신규사용일수 */ ,VMNG.NEW_REMNDR_DAYCNT /* 신규잔여일수 */ ,VMNG.ALTMNT_BGNG_YMD /* 배정시작일자 */ ,VMNG.ALTMNT_USE_END_YMD /* 배정사용종료일자 */ ,CASE WHEN DATEDIFF(DATE_FORMAT(NOW(), '%Y%m%d'), DATE_FORMAT(CONCAT(DATE_FORMAT(NOW(), '%Y'), '0401'), '%Y%m%d')) < 0 THEN DATE_FORMAT(NOW(), '%Y') - 1 ELSE DATE_FORMAT(NOW(), '%Y') END FLAG_YEAR /* 회계년도 */ FROM VCATN_MNG VMNG RIGHT JOIN EMP EMP ON EMP.EMP_ID = VMNG.EMP_ID LEFT JOIN DEPT_HNF HNF ON HNF.EMPNO = EMP.EMPNO LEFT JOIN DEPT DEPT ON DEPT.DEPT_ID = HNF.DEPT_ID ) A WHERE 1 = 1
AND VCATN_YR = FLAG_YEAR
AND VCATN_YR = #{vcatnYr}

WITH RECURSIVE VCATN_CNT AS ( SELECT CONCAT(EMP.EMP_FLNM, '(', CD.CD_NM, ')') AS TITLE ,DATE(VCATN.VCATN_BGNG_YMD) AS VCATN_BGNG_YMD ,DATE(VCATN.VCATN_END_YMD) AS VCATN_END_YMD FROM EMP EMP ,VCATN_ATRZ VCATN ,ELCTRN_ATRZ ELCTRN ,CD CD WHERE EMP.EMP_ID = ELCTRN.ATRZ_DMND_EMP_ID AND VCATN.ELCTRN_ATRZ_ID = ELCTRN.ELCTRN_ATRZ_ID AND VCATN.VCATN_TY_CD != 'VTW01207' AND CD.CD_VALUE = VCATN.VCATN_TY_CD AND ELCTRN.ATRZ_DMND_STTS_CD = 'VTW03703'
AND SUBSTR(VCATN.VCATN_BGNG_YMD, 1, 4) = #{searchYear}
AND SUBSTR(VCATN.VCATN_BGNG_YMD, 5, 2) = #{searchMonth}
AND EMP.EMP_ID = #{empId}
UNION ALL SELECT TITLE ,DATE_ADD(VCATN_BGNG_YMD, INTERVAL 1 DAY) AS VCATN_BGNG_YMD ,VCATN_END_YMD FROM VCATN_CNT WHERE VCATN_BGNG_YMD < VCATN_END_YMD ) SELECT CNT.TITLE ,CNT.VCATN_BGNG_YMD AS DATE FROM VCATN_CNT CNT ,CRTR_DATE CRTR WHERE DATE_FORMAT(VCATN_BGNG_YMD, '%Y%m%d') = CRTR.CRTR_YMD AND CRTR.HLDY_CL_CD = 'VTW05001'

mapper.insert:
/* humanResourceMngMapper.retrieveMtgRoomStrgMdfcn */
INSERT INTO MTG_ROOM_RSVT
	VALUES (
	#{mtgRoomRsvtSn}
	,#{mtgRoomCd}
	,#{rsvtEmpId}
	,#{useYmd}
	,#{useBgngHm}
	,#{useEndHm}
	,#{mtgTtl}
	,SYSDATE()
	,#{regEmpId}
	,SYSDATE()
	,#{regEmpId}
)
ON DUPLICATE KEY UPDATE
	MTG_ROOM_RSVT_SN	= #{mtgRoomRsvtSn}
	,MTG_ROOM_CD		= #{mtgRoomCd}
	,RSVT_EMP_ID		= #{rsvtEmpId}
	,USE_YMD			= #{useYmd}
	,USE_BGNG_HM		= #{useBgngHm}
	,USE_END_HM			= #{useEndHm}
	,MTG_TTL			= #{mtgTtl}
	,MDFCN_DT			= SYSDATE()
	,MDFCN_EMP_ID		= #{regEmpId}

/* humanResourceMngMapper.retrieveMtgRoomAtdrnStrgMdfcn */
INSERT INTO MTG_ROOM_RSVT_ATDRN
VALUES (
	#{mtgAtdrnSn}
	,#{mtgRoomRsvtSn}
	,#{atndEmpId}
)
ON DUPLICATE KEY UPDATE
	MTG_ATDRN_SN		= #{mtgAtdrnSn}
	,MTG_ROOM_RSVT_SN	= #{mtgRoomRsvtSn}
	,ATND_EMP_ID		= #{atndEmpId}

mapper.update:
/* humanResourceMngMapper.updateVcatnCntrlYmdYn */
UPDATE CRTR_DATE
SET VCATN_CNTRL_YMD_YN = 'Y'
WHERE CRTR_YMD BETWEEN #{cntrBgngYmd} AND #{cntrEndYmd}
