mapper.select:
/* indvdlClmMapper.retrieveVcatnListInq */ SELECT EMP.EMP_ID /* 직원ID */ ,EMP.EMP_FLNM /* 직원명 */ ,VCATN.VCATN_TY_CD /* 휴가유형코드 */ ,(SELECT CD_NM FROM CD WHERE CD_VALUE = VCATN.VCATN_TY_CD) VCATN_TY_NM /* 휴가유형코드명 */ ,DATE_FORMAT(VCATN.VCATN_BGNG_YMD, '%Y%m%d') VCATN_BGNG_YMD /* 휴가시작일자 */ ,DATE_FORMAT(VCATN.VCATN_END_YMD, '%Y%m%d' ) VCATN_END_YMD /* 휴가종료일자 */ ,DATE_FORMAT(VCATN.VCATN_BGNG_YMD, '%Y-%m-%d') PARSE_VCATN_BGNG_YMD /* 테이블휴가시작일자 */ ,DATE_FORMAT(VCATN.VCATN_END_YMD, '%Y-%m-%d' ) PARSE_VCATN_END_YMD /* 테이블휴가종료일자 */ ,VCATN.VCATN_DE_CNT /* 휴가일수 */ ,VCATN.NEW_VCATN_USE_DAYCNT /* 신규휴가사용일수 */ ,CASE WHEN VCATN.RTRCN_PRVONSH IS NOT NULL THEN VCATN.RTRCN_PRVONSH ELSE VCATN.VCATN_PRVONSH END VCATN_PRVONSH /* 휴가사유 */ ,VCATN.RTRCN_PRVONSH /* 휴가취쇼사유 */ ,ELCTRN.ATRZ_DMND_STTS_CD /* 결재요청상태코드 */ ,CASE WHEN VCATN.RTRCN_PRVONSH IS NOT NULL THEN CONCAT('취소 ', (SELECT CD_NM FROM CD WHERE CD_VALUE = ELCTRN.ATRZ_DMND_STTS_CD)) ELSE (SELECT CD_NM FROM CD WHERE CD_VALUE = ELCTRN.ATRZ_DMND_STTS_CD) END ATRZ_DMND_STTS_NM /* 결재요청상태코드명 */ ,ELCTRN.ELCTRN_ATRZ_ID /* 전자결재ID */ ,VCATN.ATCHMNFL_ID /* 첨부파일ID */ ,ELCTRN.RTRCN_ELCTRN_ATRZ_ID /* 취소전자결재ID */ ,CASE WHEN (SELECT ATRZ.RTRCN_ELCTRN_ATRZ_ID FROM ELCTRN_ATRZ ATRZ WHERE ATRZ.RTRCN_ELCTRN_ATRZ_ID = VCATN.ELCTRN_ATRZ_ID) IS NOT NULL THEN 'FALSE' WHEN ELCTRN.RTRCN_ELCTRN_ATRZ_ID IS NOT NULL THEN 'FALSE' ELSE 'TRUE' END CANCLE_BUTTONCTR FROM VCATN_ATRZ VCATN ,ELCTRN_ATRZ ELCTRN ,EMP EMP WHERE VCATN.ELCTRN_ATRZ_ID = ELCTRN.ELCTRN_ATRZ_ID AND ELCTRN.ATRZ_DMND_EMP_ID = EMP.EMP_ID
AND EMP.EMP_ID = #{empId}
AND VCATN_BGNG_YMD BETWEEN STR_TO_DATE(CONCAT(#{searchYear}, '0401'), '%Y%m%d') AND STR_TO_DATE(CONCAT(#{searchYear} + 1, '0331'), '%Y%m%d')
ORDER BY VCATN.VCATN_BGNG_YMD DESC

/* indvdlClmMapper.retrieveVcatnInfoInq */ SELECT 'ACCOUNT' VCATN_FLAG ,EMP.EMP_ID /* 직원ID */ ,EMP.EMP_FLNM /* 직원명 */ ,VMNG.VCATN_YR /* 휴가연도 */ ,VMNG.VCATN_ALTMNT_DAYCNT /* 휴가배정일수 */ ,VMNG.USE_DAYCNT /* 사용일수 */ ,VMNG.VCATN_REMNDR_DAYCNT /* 휴가잔여일수 */ ,VMNG.NEW_VCATN_ALTMNT_DAYCNT /* 신규휴가배정일수 */ ,VMNG.NEW_USE_DAYCNT /* 신규사용일수 */ ,VMNG.NEW_REMNDR_DAYCNT /* 신규잔여일수 */ ,VMNG.PBLEN_VCATN_USE_DAYCNT /* 공적휴가사용일수 */ ,VMNG.ALTMNT_BGNG_YMD /* 배정시작일자 */ ,VMNG.ALTMNT_USE_END_YMD /* 배정사용종료일자 */ FROM VCATN_MNG VMNG ,EMP EMP WHERE VMNG.EMP_ID = EMP.EMP_ID AND NEW_VCATN_YN = 'N'
AND EMP.EMP_ID = #{empId}
AND VMNG.VCATN_YR = #{searchYear}
UNION ALL SELECT 'NEW' VCATN_FLAG ,EMP.EMP_ID /* 직원ID */ ,EMP.EMP_FLNM /* 직원명 */ ,VMNG.VCATN_YR /* 휴가연도 */ ,VMNG.VCATN_ALTMNT_DAYCNT /* 휴가배정일수 */ ,VMNG.USE_DAYCNT /* 사용일수 */ ,VMNG.VCATN_REMNDR_DAYCNT /* 휴가잔여일수 */ ,VMNG.NEW_VCATN_ALTMNT_DAYCNT /* 신규휴가배정일수 */ ,VMNG.NEW_USE_DAYCNT /* 신규사용일수 */ ,VMNG.NEW_REMNDR_DAYCNT /* 신규잔여일수 */ ,VMNG.PBLEN_VCATN_USE_DAYCNT /* 공적휴가사용일수 */ ,VMNG.ALTMNT_BGNG_YMD /* 배정시작일자 */ ,VMNG.ALTMNT_USE_END_YMD /* 배정사용종료일자 */ FROM VCATN_MNG VMNG ,EMP EMP WHERE VMNG.EMP_ID = EMP.EMP_ID AND NEW_VCATN_YN = 'Y'
AND DATE_FORMAT(SYSDATE(), '%Y%m%d') BETWEEN ALTMNT_BGNG_YMD AND ALTMNT_USE_END_YMD
AND #{vcatnBgngYmd} BETWEEN ALTMNT_BGNG_YMD AND ALTMNT_USE_END_YMD
AND EMP.EMP_ID = #{empId}

/* indvdlClmMapper.retrieveAtrzLnSrngInq */ SELECT EMP.EMP_ID ,EMP.EMPNO ,EMP_FLNM ,JBPS_CD ,(SELECT CD_NM FROM CD WHERE CD_VALUE = EMP.JBPS_CD) JBPS_NM ,PRJCT.PRJCT_MNGR_EMP_ID ,CONCAT(EMP_FLNM, ' ', (SELECT CD_NM FROM CD WHERE CD_VALUE = EMP.JBPS_CD)) LIST_EMP_FLNM FROM EMP EMP ,PRJCT PRJCT WHERE HDOF_STTS_CD = 'VTW00301' AND EMP.EMP_ID = PRJCT.PRJCT_MNGR_EMP_ID
AND PRJCT.PRJCT_MNGR_EMP_ID = #{prjctMngrEmpId}
AND PRJCT.PRJCT_ID = #{prjctId}

/* indvdlClmMapper.retrieveAtrzLnAprvInq */ SELECT EMP.EMP_ID ,EMP_FLNM ,JBPS_CD ,(SELECT CD_NM FROM CD WHERE CD_VALUE = JBPS_CD) JBPS_NM ,DEPT.DEPT_ID ,DEPT.DEPT_NM ,HNF.JBTTL_CD ,(SELECT CD_NM FROM CD WHERE HNF.JBTTL_CD = CD_VALUE) JBTTL_NM ,CONCAT(DEPT_NM, ' ', EMP_FLNM, ' ', (SELECT CD_NM FROM CD WHERE CD_VALUE = EMP.JBPS_CD)) LIST_EMP_FLNM FROM EMP EMP ,DEPT DEPT ,DEPT_HNF HNF WHERE HDOF_STTS_CD = 'VTW00301' AND EMP.EMP_ID = HNF.EMP_ID AND DEPT.DEPT_ID = HNF.DEPT_ID AND EMP.EMP_ID != #{empId}
AND DEPT.DEPT_ID IN
#{item.deptId}
ORDER BY JBTTL_CD

mapper.insert:
/* indvdlClmMapper.retrievePrjctIndvdlCtMmStrg */
INSERT INTO PRJCT_INDVDL_CT_MM
VALUES (
    #{prjctId}			/* 프로젝트ID */
    ,#{empId}			/* 직원ID */
    ,#{aplyYm}			/* 신청연월 */
    ,#{aplyOdr}			/* 신청차수 */
    ,NULL            	/* 비용결재완료여부 */
    ,'N'            	/* MM결재완료여부 */
    ,#{expensCd}		/* 경비코드 */
)
ON DUPLICATE KEY UPDATE
    PRJCT_ID 			= #{prjctId}
    ,EMP_ID				= #{empId}
    ,APLY_YM 			= #{aplyYm}
    ,APLY_ODR			= #{aplyOdr}
    ,MM_ATRZ_CMPTN_YN 	= 'N'

mapper.delete:
/* indvdlClmMapper.retrievePrjctMmAplyDel */
DELETE FROM PRJCT_MM_APLY
WHERE EMP_ID = #{empId}
AND APLY_YM = #{aplyYm}
AND APLY_ODR = #{aplyOdr}
AND APLY_YMD = #{aplyYmd}
AND PRJCT_ID = #{prjctId}

/* indvdlClmMapper.retrievePrjctMmAtrzDel */
DELETE FROM PRJCT_MM_ATRZ
WHERE EMP_ID = #{empId}
AND APLY_YM = #{aplyYm}
AND APLY_ODR = #{aplyOdr}
AND APLY_YMD = #{aplyYmd}
AND PRJCT_ID = #{prjctId}

mapper.update:
/* indvdlClmMapper.retrievePrjctMmSttsInq */
UPDATE PRJCT_MM_ATRZ
SET
    ATRZ_DMND_STTS_CD = ''
    ,MDFCN_DT = SYSDATE()
    ,MDFCN_EMP_ID = #{empId}
WHERE EMP_ID = #{empId}
AND APLY_YM = #{aplyYm}
AND APLY_ODR = #{aplyOdr}
AND APLY_YMD = #{aplyYmd}
AND PRJCT_ID = #{prjctId}

mapper.delete:
/* indvdlClmMapper.retrievePrjctMmAplySttsDel */
DELETE FROM PRJCT_MM_APLY
WHERE EMP_ID = #{empId}
AND APLY_YM = #{aplyYm}
AND APLY_ODR = #{aplyOdr}
AND APLY_YMD = #{aplyYmd}
AND PRJCT_ID = #{prjctId}

mapper.update:
/* indvdlClmMapper.retrieveVcatnAltmntMngMdfcn */
UPDATE
    VCATN_MNG
SET
    MDFCN_DT 					= SYSDATE()						/* 수정일시 */
    ,MDFCN_EMP_ID 				= #{mdfcnEmpId}					/* 수정직원ID */

        ,USE_DAYCNT					= #{useDaycnt}              /* 사용일수 */
        ,VCATN_REMNDR_DAYCNT	    = #{vcatnRemndrDaycnt}      /* 휴가잔여일수 */


        ,PBLEN_VCATN_USE_DAYCNT	= #{pblenVcatnUseDaycnt}		/* 공적휴가사용일수 */

WHERE EMP_ID = #{empId}
AND VCATN_YR = #{vcatnYr}
AND NEW_VCATN_YN = #{newVcatnYn}

/* indvdlClmMapper.retrieveNewVcatnAltmntMngMdfcn */
UPDATE
    VCATN_MNG
SET
    NEW_USE_DAYCNT	            = #{newUseDaycnt}				/* 신규사용일수 */
    ,NEW_REMNDR_DAYCNT	        = #{newRemndrDaycnt}			/* 신규잔여일수 */
    ,MDFCN_DT 					= SYSDATE()						/* 수정일시 */
    ,MDFCN_EMP_ID 				= #{mdfcnEmpId}					/* 수정직원ID */
WHERE EMP_ID = #{empId}
AND VCATN_YR = #{vcatnYr}

mapper.select:
SELECT CARD_USE_SN, EMP_ID, PRJCT_ID, EXPENS_CD, ATDRN, CT_PRPOS, APLY_YM, APLY_ODR, CONCAT(SUBSTR(UTZTN_DT, 5, 2),'월 ',SUBSTR(UTZTN_DT,7,2),'일 ',SUBSTR(UTZTN_DT,9,2),':',SUBSTR(UTZTN_DT,11,2)) AS UTZTN_DT_FORMAT, UTZTN_DT, USE_OFFIC, UTZTN_AMT, APRV_NO FROM CARD_USE_DTLS cud WHERE 1 = 1 AND cud.PRJCT_CT_INPT_PSBLTY_YN = 'Y'
AND cud.EMP_ID = #{empId}
AND cud.APLY_YM = #{aplyYm}
AND cud.APLY_ODR = #{aplyOdr}
AND cud.USE_OFFIC LIKE CONCAT('%', #{useOffic}, '%')
AND SUBSTR(UTZTN_DT,1,8) >= #{startDate}
AND SUBSTR(UTZTN_DT,1,8) <= #{endDate}
AND SUBSTR(UTZTN_DT,1,8) BETWEEN #{startDate} AND #{endDate}
AND TIME(UTZTN_DT) BETWEEN (SELECT SUBSTR(CD_NM,4,5) FROM CD WHERE CD_VALUE = #{useTime}) AND (SELECT SUBSTR(CD_NM,10,5) FROM CD WHERE CD_VALUE = #{useTime})
ORDER BY cud.UTZTN_DT ASC

SELECT COUNT(1) AS CNT FROM PRJCT_CT_APLY pca JOIN PRJCT_CT_ATRZ pcaz ON pca.PRJCT_CT_APLY_SN = pcaz.PRJCT_CT_APLY_SN AND pca.EMP_ID = pcaz.EMP_ID AND pca.APLY_YM = pcaz.APLY_YM AND pca.APLY_ODR = pcaz.APLY_ODR WHERE 1 = 1 AND pca.EXPENS_CD = 'VTW04509' -- 스마트폰지원 AND pcaz.ATRZ_DMND_STTS_CD NOT IN ('VTW03701','VTW03702','VTW03703') -- 임시저장, 결재중, 승인
AND pca.EMP_ID = #{empId}
AND pca.APLY_YM = #{aplyYm}

SELECT MAX(PRJCT_CT_APLY_SN) AS PRJCT_CT_APLY_SN
FROM PRJCT_CT_APLY pca
WHERE 1 = 1

    AND pca.EMP_ID = #{empId}


    AND pca.PRJCT_ID = #{prjctId}


    AND pca.APLY_YM = #{aplyYm}


    AND pca.APLY_ODR = #{aplyOdr}
