mapper.select:
SELECT E.EMPNO AS EMPNO, E.EMP_FLNM AS EMP_FLNM, C.CD_NM AS JBPS_NM, D.DEPT_NM AS DEPT_NM, E.TELNO AS TELNO, E.EML AS EML, C2.CD_NM AS HODF_STTS_NM , COUNT(*) OVER () AS TOTAL_ITEMS ,C3.CD_NM AS BANK_NM ,E.ACTNO AS ACTNO FROM EMP E LEFT OUTER JOIN CD C ON C.CD_VALUE =E.JBPS_CD LEFT OUTER JOIN DEPT_HNF DF ON E.EMP_ID = DF.EMP_ID LEFT OUTER JOIN DEPT D ON D.DEPT_ID = DF.DEPT_ID LEFT OUTER JOIN CD C2 ON C2.CD_VALUE =E.HDOF_STTS_CD LEFT OUTER JOIN CD C3 ON C3.CD_VALUE =E.BANK_CD WHERE 1=1
AND E.EMPNO LIKE CONCAT('%', #{empno}, '%')
AND E.EMP_FLNM LIKE CONCAT('%', #{empFlnm}, '%')
AND E.JBPS_CD = #{jbpsNm}
AND D.DEPT_NM = #{deptNm}
AND E.TELNO LIKE CONCAT('%', #{telNo}, '%')
AND C2.CD_VALUE = #{hodfSttsNm}
