mapper.select:
SELECT PRJCT_CD_IDNTFR , (SELECT CD_NM FROM CD WHERE CD_VALUE = PRJCT_STLE_CD) AS PRJCT_STLE_CD , p.PRJCT_ID , PRJCT_NM , (SELECT CD_NM FROM CD WHERE CD_VALUE = BIZ_STTS_CD) AS BIZ_STTS_CD_NM , BIZ_STTS_CD , (SELECT EMP_FLNM FROM EMP WHERE EMP_ID = PRJCT_MNGR_EMP_ID ) AS PRJCT_MNGR_EMP_ID , (SELECT CTMMNY_NM FROM CTMMNY_INFO ci WHERE ci.CTMMNY_ID = p.CTMMNY_ID) AS CTMMNY_NM , DATE_FORMAT(CTRT_YMD, '%Y-%m-%d') AS CTRT_YMD , DATE_FORMAT(BIZ_END_YMD, '%Y-%m-%d') AS BIZ_END_YMD , DATE_FORMAT(STBLE_END_YMD, '%Y-%m-%d') AS STBLE_END_YMD , TOT_BGT , (SELECT MAX(BGT_MNG_ODR) FROM PRJCT_BGT_PRMPC pbp WHERE pbp.PRJCT_ID = p.PRJCT_ID AND pbp.ATRZ_DMND_STTS_CD = 'VTW03703') AS BGT_MNG_ODR , (SELECT IFNULL(MAX(BGT_MNG_ODR), 1) FROM PRJCT_BGT_PRMPC pbp WHERE pbp.PRJCT_ID = p.PRJCT_ID) AS BGT_MNG_ODR_TOBE , PRJCT_CD_IDNTFR , DEPT_ID , COUNT(*) OVER () AS TOTAL_ITEMS FROM PRJCT p
JOIN PRJCT_MNG_AUTHRT PMA ON (PMA.PRJCT_ID = p.PRJCT_ID AND PMA.EMP_ID = #{empId})
WHERE 1 = 1
AND p.PRJCT_STLE_CD = #{prjctStleCd}
AND p.PRJCT_ID = #{prjctId}
AND p.BIZ_STTS_CD = #{bizFlfmtTyCd}
AND p.CTRT_YMD >= #{ctrtYmd}
AND p.BIZ_END_YMD <= #{bizEndYmd}
AND p.CTRT_YMD BETWEEN #{ctrtYmd} AND #{bizEndYmd}
AND CTMMNY_ID IN (SELECT CTMMNY_ID FROM CTMMNY_INFO WHERE CTMMNY_NM LIKE CONCAT('%',#{ctmmnyNm},'%'))
ORDER BY p.CTRT_YMD DESC

SELECT ROW_NUMBER()OVER ()ROWNUM , (SELECT CD_NM FROM CD WHERE CD_VALUE = PRJCT_STLE_CD) AS PRJCT_STLE_CD , p.PRJCT_ID , PRJCT_NM , (SELECT CD_NM FROM CD WHERE CD_VALUE = BIZ_STTS_CD) AS BIZ_STTS_CD_NM , BIZ_STTS_CD , (SELECT EMP_FLNM FROM EMP WHERE EMP_ID = PRJCT_MNGR_EMP_ID ) AS PRJCT_MNGR_EMP_ID , (SELECT CTMMNY_NM FROM CTMMNY_INFO ci WHERE ci.CTMMNY_ID = p.CTMMNY_ID) AS CTMMNY_NM , DATE_FORMAT(CTRT_YMD, '%Y-%m-%d') AS CTRT_YMD , DATE_FORMAT(BIZ_END_YMD, '%Y-%m-%d') AS BIZ_END_YMD , DATE_FORMAT(STBLE_END_YMD, '%Y-%m-%d') AS STBLE_END_YMD , TOT_BGT , (SELECT MAX(BGT_MNG_ODR) FROM PRJCT_BGT_PRMPC pbp WHERE pbp.PRJCT_ID = p.PRJCT_ID AND pbp.ATRZ_DMND_STTS_CD = 'VTW03703') AS BGT_MNG_ODR , (SELECT IFNULL(MAX(BGT_MNG_ODR), 1) FROM PRJCT_BGT_PRMPC pbp WHERE pbp.PRJCT_ID = p.PRJCT_ID) AS BGT_MNG_ODR_TOBE , PRJCT_CD_IDNTFR , DEPT_ID , COUNT(*) OVER () AS TOTAL_ITEMS FROM PRJCT p JOIN PRJCT_MNG_AUTHRT PMA ON (PMA.PRJCT_ID = p.PRJCT_ID AND PMA.EMP_ID = #{empId}) WHERE 1 = 1 AND p.PRJCT_MNGR_EMP_ID = #{empId}
AND p.PRJCT_STLE_CD = #{prjctStleCd}
AND p.PRJCT_ID = #{prjctId}
AND p.BIZ_STTS_CD = #{bizFlfmtTyCd}
AND p.CTRT_YMD >= #{ctrtYmd}
AND p.BIZ_END_YMD <= #{bizEndYmd}
AND p.CTRT_YMD BETWEEN #{ctrtYmd} AND #{bizEndYmd}
AND CTMMNY_ID IN (SELECT CTMMNY_ID FROM CTMMNY_INFO WHERE CTMMNY_NM LIKE CONCAT('%',#{ctmmnyNm},'%'))
ORDER BY p.REG_DT DESC

SELECT ROW_NUMBER() OVER() ROWNUM, A.* FROM ( SELECT pal.PRJCT_ID , (SELECT CD_NM FROM CD WHERE CD_VALUE = pal.PRMPC_INPT_SE_CD) AS PRMPC_INPT_SE_CD , p.PRJCT_NM , (SELECT CD_NM FROM CD WHERE CD_VALUE = pald.ATRZ_STEP_CD) AS ATRZ_STEP_CD_NM , pald.ATRZ_STEP_CD , (SELECT CD_NM FROM CD WHERE CD_VALUE = pald.ATRZ_STTS_CD) AS ATRZ_STTS_CD_NM , pald.ATRZ_STTS_CD , e.EMP_FLNM , DATE_FORMAT(pald.APRV_YMD, '%Y-%m-%d') AS APRV_YMD , DATE_FORMAT(pald.RJCT_YMD, '%Y-%m-%d') AS RJCT_YMD , pal.ATRZ_LN_SN , pal.NOW_ATRZ_STEP_CD , pal.BGT_MNG_ODR , pald.APRVR_EMP_ID , DATE_FORMAT(p.CTRT_YMD, '%Y-%m-%d') AS CTRT_YMD , DATE_FORMAT(STBLE_END_YMD, '%Y-%m-%d') AS STBLE_END_YMD , COUNT(*) OVER () AS TOTAL_ITEMS FROM PRJCT_ATRZ_LN pal JOIN PRJCT_ATRZ_LN_DTL pald ON(pal.PRJCT_ID = pald.PRJCT_ID AND pal.ATRZ_LN_SN = pald.ATRZ_LN_SN AND CASE WHEN pal.NOW_ATRZ_STEP_CD = 'VTW00708' THEN 'VTW00705' = pald.ATRZ_STEP_CD WHEN pal.NOW_ATRZ_STEP_CD != 'VTW00708' THEN pal.NOW_ATRZ_STEP_CD = pald.ATRZ_STEP_CD END ) JOIN PRJCT p ON (p.PRJCT_ID = pal.PRJCT_ID AND p.PRJCT_ID = pald.PRJCT_ID) JOIN EMP e ON (e.EMP_ID = pal.REG_EMP_ID) WHERE 1=1
AND (pald.APRVR_EMP_ID = #{empId} OR pal.REG_EMP_ID = #{empId})
AND (pald.APRVR_EMP_ID = #{empId} OR pal.REG_EMP_ID = #{empId})
AND pal.PRMPC_INPT_SE_CD = #{prmpcInptSeCd}
AND pal.PRJCT_ID = #{prjctId}
AND ATRZ_STTS_CD = #{atrzSttsCd}
AND e.EMP_FLNM LIKE CONCAT('%', #{prjctMngrEmpId}, '%')
AND pald.ATRZ_STTS_CD != 'VTW00805' ORDER BY pal.REG_DT DESC ) A

SELECT #{id} AS ID,#{costKind} AS COST_KIND,T.COST_AMT, T.USE_AMT, (T.COST_AMT - T.USE_AMT) AS AVAIL_AMT, TRUNCATE(ROUND(T.USE_AMT / T.COST_AMT * 100, 4), 2) AS USE_RATE, #{group} AS BIND
FROM
    (SELECT
         (SELECT IFNULL(SUM( ${costCol} ),0)  FROM ${tbCostNm} A WHERE PRJCT_ID =#{prjctId} AND BGT_MNG_ODR = #{bgtMngOdr}


             AND EXPENS_CD BETWEEN 'VTW04528' AND 'VTW04539'




                AND EXPENS_CD BETWEEN 'VTW04501' AND 'VTW04527'


         ) AS COST_AMT,
         (SELECT IFNULL(SUM( ${excuteCol} ),0) FROM ${tbExcuteNm} A WHERE PRJCT_ID =#{prjctId}


                AND EXPENS_CD BETWEEN 'VTW04528' AND 'VTW04539'




                AND EXPENS_CD BETWEEN 'VTW04501' AND 'VTW04527'


         ) AS USE_AMT
     FROM DUAL
    ) T

SELECT DISTINCT(EXPENS_CD) AS EXPENS_CD, (SELECT CD_NM FROM CD WHERE CD_VALUE = EXPENS_CD) AS EXPENS_NM
  FROM EXPENS_PRMPC
 WHERE PRJCT_ID = #{prjctId}
  AND BGT_MNG_ODR = #{bgtMngOdr}
  AND EXPENS_CD IN ((SELECT CD_VALUE FROM CD WHERE UP_CD_VALUE = 'VTW045' AND USER_DFN_VALUE = #{costFlag}))
ORDER BY EXPENS_CD

SELECT (SELECT CD_NM FROM CD WHERE CD_VALUE = pal.PRMPC_INPT_SE_CD) AS PRMPC_INPT_SE_CD , e2.EMP_FLNM AS REG_EMP_FLNM , (SELECT APRV_YMD FROM PRJCT_ATRZ_LN_DTL WHERE ATRZ_STEP_CD = 'VTW00705'
AND ATRZ_LN_SN = #{atrzLnSn}
AND PRJCT_ID = #{prjctId}
) AS APRV_YMD , (SELECT RJCT_YMD FROM PRJCT_ATRZ_LN_DTL WHERE RJCT_YMD IS NOT NULL
AND ATRZ_LN_SN = #{atrzLnSn}
AND PRJCT_ID = #{prjctId}
) AS RJCT_YMD , pal.ATRZ_APLY_PRVONSH_CN , concat(c1.CD_NM, ' (', c2.CD_NM, ')') AS ATRZ_STEP_STTS , c1.CD_NM AS ATRZ_STEP_NM , c2.CD_NM AS ATRZ_STTS_NM , e1.EMP_FLNM AS APRVR_EMP_FLNM , pald.ATRZ_OPNN_CN , pald.RJCT_PRVONSH , pald.APRV_YMD AS STEP_APRV_YMD , pald.RJCT_YMD AS STEP_RJCT_YMD , CASE WHEN pald.APRV_YMD IS NOT NULL THEN DATE_FORMAT(pald.MDFCN_DT, '%Y.%m.%d %H:%i:%s') WHEN pald.RJCT_YMD IS NOT NULL THEN DATE_FORMAT(pald.MDFCN_DT, '%Y.%m.%d %H:%i:%s') WHEN pald.APRV_YMD IS NULL THEN NULL WHEN pald.RJCT_YMD IS NULL THEN NULL END AS MDFCN_DT FROM PRJCT_ATRZ_LN_DTL pald JOIN CD c1 ON (c1.CD_VALUE = pald.ATRZ_STEP_CD) JOIN CD c2 ON (c2.CD_VALUE = pald.ATRZ_STTS_CD) JOIN EMP e1 ON (pald.APRVR_EMP_ID = e1.EMP_ID) JOIN PRJCT_ATRZ_LN pal ON (pal.PRJCT_ID = pald.PRJCT_ID AND pal.ATRZ_LN_SN = pald.ATRZ_LN_SN) JOIN EMP e2 ON (pal.REG_EMP_ID = e2.EMP_ID) WHERE 1=1
AND pald.PRJCT_ID = #{prjctId}
AND pald.ATRZ_LN_SN = #{atrzLnSn}

SELECT MAX(ATRZ_LN_SN) AS ATRZ_LN_SN
  FROM PRJCT_ATRZ_LN
 WHERE PRJCT_ID = #{prjctId};

