mapper.select:
SELECT ATRZ_DMND_STTS_CD 
  FROM PRJCT_BGT_PRMPC pbp 
 WHERE 1=1
   AND PRJCT_ID = #{prjctId}
   AND BGT_MNG_ODR = #{bgtMngOdr}

SELECT dept.DEPT_NM , e.EMP_FLNM , CONCAT(pma.APLY_YM, '-', pma.APLY_ODR) APLY_SN , IFNULL(mmp.EXPECT_MM,0) AS EXPECT_MM , TRUNCATE(SUM(8 * pma.MD),0) AS TOT_MD , pma.EMP_ID , pma.APLY_YM , pma.APLY_ODR , ROUND(SUM(CASE WHEN pmat.ATRZ_DMND_STTS_CD='VTW03703' THEN pma.MD ELSE 0 END)/MM_QUERY.MM,2) AS EXCN_MM , ROUND((IFNULL(mmp.EXPECT_MM,0) - ROUND(SUM(CASE WHEN pmat.ATRZ_DMND_STTS_CD='VTW03703' THEN pma.MD ELSE 0 END)/MM_QUERY.MM,2)),2) AS AVBLE_MM , CASE WHEN COUNT(*) = SUM(CASE WHEN pmat.ATRZ_DMND_STTS_CD = 'VTW03703' THEN 1 ELSE 0 END) THEN 'VTW03703' WHEN SUM(CASE WHEN pmat.ATRZ_DMND_STTS_CD = 'VTW03704' THEN 1 ELSE 0 END) > 0 THEN 'VTW03704' ELSE 'VTW03702' END AS STTS_CD FROM (SELECT (SELECT COUNT(CRTR_YMD) FROM CRTR_DATE WHERE HLDY_CL_CD = 'VTW05001' AND DATE_FORMAT(CRTR_YMD, '%Y%m') = #{aplyYm}) AS MM ) AS MM_QUERY, PRJCT_MM_APLY pma JOIN (SELECT MAX(BGT_MNG_ODR) AS BGT_MNG_ODR FROM PRJCT_BGT_PRMPC pbp WHERE pbp.PRJCT_ID = #{prjctId} AND pbp.ATRZ_DMND_STTS_CD = 'VTW03703') odr LEFT JOIN (SELECT mlp.PRJCT_ID, mlp.EMP_ID, mlp.MMNY_LBRCO_PRMPC_SN, mim.EXPECT_MM, mlp.BGT_MNG_ODR FROM MMNY_LBRCO_PRMPC mlp JOIN MMNY_INPT_MM mim ON (mim.PRJCT_ID = mlp.PRJCT_ID AND mim.MMNY_LBRCO_PRMPC_SN = mlp.MMNY_LBRCO_PRMPC_SN AND mlp.BGT_MNG_ODR = mim.BGT_MNG_ODR) WHERE 1=1 AND mim.INPT_YM = #{aplyYm} AND mim.PRJCT_ID = #{prjctId}) mmp ON (mmp.EMP_ID = pma.EMP_ID AND mmp.BGT_MNG_ODR = odr.BGT_MNG_ODR) JOIN EMP e ON (e.EMP_ID = pma.EMP_ID) LEFT JOIN PRJCT_MM_ATRZ pmat ON pmat.PRJCT_ID = pma.PRJCT_ID AND (pmat.EMP_ID = pma.EMP_ID OR (pmat.EMP_ID IS NULL AND pma.EMP_ID IS NULL)) AND pmat.APLY_YM = pma.APLY_YM AND pmat.APLY_ODR = pma.APLY_ODR AND pmat.APLY_YMD = pma.APLY_YMD JOIN ( SELECT GROUP_CONCAT(DISTINCT d.DEPT_NM) AS DEPT_NM, pma.EMP_ID FROM DEPT d JOIN DEPT_HNF dh ON (d.DEPT_ID = dh.DEPT_ID) JOIN PRJCT_MM_APLY pma ON (pma.EMP_ID = dh.EMP_ID) GROUP BY pma.EMP_ID ) dept ON dept.EMP_ID = e.EMP_ID WHERE 1=1 AND pma.PRJCT_ID = #{prjctId} AND pma.APLY_YM = #{aplyYm} AND pma.APLY_ODR = #{aplyOdr} AND pmat.ATRZ_DMND_STTS_CD NOT IN ('VTW03701', 'VTW03708')
AND pma.EMP_ID = #{empId}
GROUP BY pma.EMP_ID, mmp.EXPECT_MM, MM_QUERY.MM

