SELECT CD_VALUE AS MATRL_CT_IEM_CD, CD_NM AS MATRL_CT_IEM_CD_NM FROM CD 
where 1=1
and UP_CD_VALUE = #{upCdValue}
and USE_YN = #{useYn}

SELECT dept.DEPT_NM , e.EMP_FlNM , CONCAT(pca.APLY_YM, '-', pca.APLY_ODR) AS APLY_SN , SUM(UTZTN_AMT) AS TOT_APLY_AMT , SUM(CASE WHEN pcat.ATRZ_DMND_STTS_CD='VTW03703' AND pcat.PRJCT_CT_APLY_SN = pca.PRJCT_CT_APLY_SN THEN UTZTN_AMT ELSE 0 END) AS TOT_UTZTN_AMT , e.EMP_ID , pca.APLY_YM , pca.APLY_ODR , CASE WHEN COUNT(*) = SUM(CASE WHEN pcat.ATRZ_DMND_STTS_CD = 'VTW03703' THEN 1 ELSE 0 END) THEN 'VTW03703' WHEN SUM(CASE WHEN pcat.ATRZ_DMND_STTS_CD = 'VTW03704' THEN 1 ELSE 0 END) > 0 THEN 'VTW03704' ELSE 'VTW03702' END AS STTS_CD FROM PRJCT_CT_APLY pca JOIN EMP e ON (e.EMP_ID = pca.EMP_ID) JOIN ( SELECT GROUP_CONCAT(DISTINCT d.DEPT_NM) AS DEPT_NM, pca.EMP_ID FROM DEPT d JOIN DEPT_HNF dh ON (d.DEPT_ID = dh.DEPT_ID) JOIN PRJCT_CT_APLY pca ON (pca.EMP_ID = dh.EMP_ID) GROUP BY pca.EMP_ID ) dept ON dept.EMP_ID = e.EMP_ID LEFT JOIN PRJCT_CT_ATRZ pcat ON (pcat.PRJCT_ID = pca.PRJCT_ID AND (pcat.EMP_ID = pca.EMP_ID OR (pcat.EMP_ID IS NULL AND pca.EMP_ID IS NULL)) AND pcat.APLY_YM = pca.APLY_YM AND pcat.APLY_ODR = pca.APLY_ODR AND pcat.PRJCT_CT_APLY_SN = pca.PRJCT_CT_APLY_SN) WHERE 1=1 AND pca.PRJCT_ID = #{prjctId} AND pca.APLY_YM = #{aplyYm} AND pca.APLY_ODR = #{aplyOdr} AND pcat.ATRZ_DMND_STTS_CD IS NOT NULL AND pcat.ATRZ_DMND_STTS_CD NOT IN ('VTW03701', 'VTW03708')
AND pca.EMP_ID = #{empId}
GROUP BY e.EMP_ID

mapper.update:
UPDATE PRJCT_CT_ATRZ pcat
JOIN PRJCT_CT_APLY pca ON (pcat.EMP_ID = pca.EMP_ID AND pcat.APLY_YM = pca.APLY_YM
    AND pcat.APLY_ODR = pca.APLY_ODR AND pcat.PRJCT_ID = pca.PRJCT_ID AND pcat.PRJCT_CT_APLY_SN = pca.PRJCT_CT_APLY_SN)
SET
    pcat.ATRZ_DMND_STTS_CD = 'VTW03702'
  , pcat.APRVR_EMP_ID = null
  , pcat.APRV_YMD = null
WHERE pcat.EMP_ID = #{empId}
  AND pcat.APLY_YM = #{aplyYm}
  AND pcat.APLY_ODR = #{aplyOdr}

  AND pcat.PRJCT_ID = #{prjctId}

  AND pcat.ATRZ_DMND_STTS_CD = 'VTW03703'
  AND pca.ELCTRN_ATRZ_ID IS NULL

mapper.select:
        SELECT 
			#{id} AS ID, 


					#{costKind} AS COST_KIND, 


					A.CNSRTM_CO_NM AS COST_KIND,


			A.CN AS COST_AMT,
		    ROUND(A.CN / B.TOTAL * 100, 2) AS RATE, 
			#{group} AS BIND
		FROM 

			    (SELECT 
			        TOT_BGT AS CN
			     FROM 
			        PRJCT M
			     WHERE 
			        M.PRJCT_ID = #{prjctId}
			    ) A,



			    (SELECT 
			        CNSRTM_CO_NM, CMMN_EXPENS_AMT AS CN
			     FROM 
			        PRJCT_CNSRTM M
			     WHERE 1=1
			        AND M.PRJCT_ID = #{prjctId}
			        AND M.SLS_AM_RFLT_YN = 'Y'	 
			    ) A,


			    (SELECT SUM(T.TOTAL) AS CN
					FROM (
					SELECT (S.UNTPC * S.EXPECT_MM)AS TOTAL  FROM MMNY_LBRCO_PRMPC M
					JOIN MMNY_INPT_MM S
					ON M.PRJCT_ID = S.PRJCT_ID AND M.BGT_MNG_ODR = S.BGT_MNG_ODR AND M.MMNY_LBRCO_PRMPC_SN = S.MMNY_LBRCO_PRMPC_SN 
					WHERE 1=1
					AND M.PRJCT_ID = #{prjctId}
					AND M.BGT_MNG_ODR = #{bgtMngOdr}
					)T
				  ) A ,


			    ( SELECT SUM(T.TOTAL) AS CN
					FROM (
					SELECT (S.UNTPC * S.EXPECT_MM)AS TOTAL  FROM OUTORD_LBRCO_PRMPC M
					JOIN OUTORD_LBRCO_PRMPC_DTL S
					ON M.PRJCT_ID = S.PRJCT_ID AND M.BGT_MNG_ODR = S.BGT_MNG_ODR AND M.OUTORD_LBRCO_PRMPC_SN = S.OUTORD_LBRCO_PRMPC_SN 
					WHERE 1=1
					AND M.PRJCT_ID = #{prjctId}
					AND M.BGT_MNG_ODR = #{bgtMngOdr}
					)T
				  ) A ,


			    ( SELECT SUM(T.TOTAL) AS CN
					FROM (
					SELECT EXPECT_CT AS TOTAL  FROM OUTORD_ENTRPS_CT_PRMPC M
					WHERE 1=1
					AND M.PRJCT_ID = #{prjctId}
					AND M.BGT_MNG_ODR = #{bgtMngOdr}
					)T
				  ) A ,


			    ( SELECT SUM(T.TOTAL) AS CN
					FROM (
					SELECT CTRT_AMT AS TOTAL  FROM MATRL_CT_PRMPC M
					WHERE 1=1
					AND M.PRJCT_ID = #{prjctId}
					AND M.BGT_MNG_ODR = #{bgtMngOdr}
					)T
				  ) A ,


			    ( SELECT SUM(T.TOTAL) AS CN
					FROM (
					SELECT EXPECT_CT AS TOTAL  FROM EXPENS_PRMPC M
					JOIN EXPENS_MNBY_PRMPC_DTLS S
					ON M.PRJCT_ID = S.PRJCT_ID AND M.BGT_MNG_ODR = S.BGT_MNG_ODR AND M.EXPENS_PRMPC_SN = S.EXPENS_PRMPC_SN 
					WHERE 1=1

						AND M.EXPENS_CD BETWEEN 'VTW04528' AND 'VTW04535'


						AND M.EXPENS_CD BETWEEN 'VTW04501' AND 'VTW04527'

					AND M.PRJCT_ID =  #{prjctId}
					AND M.BGT_MNG_ODR = #{bgtMngOdr}
					)T
				  ) A ,

		    (
			  SELECT (COALESCE(T.TOT_BGT, 0) + SUM(COALESCE(T.CMMN_EXPENS_AMT, 0))) AS TOTAL
				FROM
				(SELECT M.TOT_BGT, M.PRJCT_ID,S.CNSRTM_CO_NM,S.CMMN_EXPENS_AMT FROM PRJCT M
				LEFT JOIN PRJCT_CNSRTM S
				ON M.PRJCT_ID = S.PRJCT_ID AND S.SLS_AM_RFLT_YN  = 'Y'
				WHERE 1=1
				AND M.PRJCT_ID = #{prjctId}
				) T
			 )B

SELECT PRJCT_ID
	 , ATRZ_LN_SN
	 , ATRZ_STEP_CD
	 , APRVR_EMP_ID
 FROM PRJCT_ATRZ_LN_DTL
WHERE 1=1
  AND PRJCT_ID = #{prjctId}
  AND ATRZ_LN_SN = #{atrzLnSn}
  AND ATRZ_STEP_CD  >=  #{atrzStepCd}

SELECT EMP_ID , EMPNO , EMP_FLNM , JBPS_NM , DEPT_ID , HDOF_STTS_NM , READ_YN , WRITE_YN FROM ( SELECT E.EMP_ID AS EMP_ID , E.EMPNO AS EMPNO , E.EMP_FLNM AS EMP_FLNM , C.CD_NM AS JBPS_NM , GROUP_CONCAT(DISTINCT D.DEPT_NM) AS DEPT_ID , C2.CD_NM AS HDOF_STTS_NM , CASE WHEN PMA.EMP_ID IS NOT NULL AND PMA.PRJCT_MNG_AUTHRT_CD = 'VTW05201' OR PMA.PRJCT_MNG_AUTHRT_CD = 'VTW05202' THEN 'Y' WHEN PMA.EMP_ID IS NULL THEN 'N' ELSE 'N' END AS READ_YN , CASE WHEN PMA.EMP_ID IS NOT NULL AND PMA.PRJCT_MNG_AUTHRT_CD = 'VTW05202' THEN 'Y' WHEN PMA.EMP_ID IS NULL THEN 'N' ELSE 'N' END AS WRITE_YN FROM EMP E LEFT OUTER JOIN CD C ON C.CD_VALUE = E.JBPS_CD LEFT OUTER JOIN DEPT_HNF DF ON E.EMP_ID = DF.EMP_ID LEFT OUTER JOIN DEPT D ON D.DEPT_ID = DF.DEPT_ID LEFT OUTER JOIN CD C2 ON C2.CD_VALUE = E.HDOF_STTS_CD LEFT OUTER JOIN CD C3 ON C3.CD_VALUE = E.BANK_CD LEFT OUTER JOIN EMP_HIST EH ON EH.EMP_ID = E.EMP_ID LEFT OUTER JOIN PRJCT_MNG_AUTHRT PMA ON E.EMP_ID = PMA.EMP_ID AND PMA.PRJCT_ID = #{prjctId} LEFT OUTER JOIN PRJCT P ON P.PRJCT_ID = PMA.PRJCT_ID WHERE 1=1
AND E.EMPNO LIKE CONCAT('%', #{empno}, '%')
AND E.EMP_FLNM LIKE CONCAT('%', #{empFlnm}, '%')
AND E.JBPS_CD = #{jbpsNm}
AND E.TELNO LIKE CONCAT('%', #{telNo}, '%')
AND D.DEPT_ID = #{deptId}
AND C2.CD_VALUE = #{hdofSttsNm}
AND E.EMP_TY_CD != 'VTW00203' AND E.EMPNO NOT IN ('VK0101', 'VK0102', 'VK0103', 'VK0104', 'VK0105', 'VK0106', 'VK0107', 'VK0108', 'VK0109', 'VK0110', 'VM1000') AND D.END_YN = 'N' GROUP BY E.EMP_ID, E.EMPNO, E.EMP_FLNM, E.EMP_TY_CD, C.CD_NM, E.TELNO, E.EML, C2.CD_NM, C3.CD_NM, E.ACTNO, C.CD_VALUE, C3.CD_VALUE, C2.CD_VALUE,READ_YN, WRITE_YN ORDER BY E.EMPNO ASC ) A

SELECT
    COUNT(*) AS TOTAL_COUNT
FROM PRJCT_MM_ATRZ
WHERE ATRZ_DMND_STTS_CD NOT IN ('VTW03703')
  AND PRJCT_ID = #{prjctId}
  AND APLY_YM = #{aplyYm}
  AND APLY_ODR = #{aplyOdr}
  AND EMP_ID = #{empId}

