mapper.select:
SELECT LU.EMP_ID, LU.EMPNO, E.EMP_FLNM AS EMP_NM, (SELECT CD_NM FROM CD WHERE CD_VALUE = E.JBPS_CD) JBPS_NM, E.JBPS_CD, E.EMP_TY_CD, PSWD, INTL_PWSD_YN FROM LGN_USER LU JOIN EMP E ON LU.EMP_ID = E.EMP_ID WHERE 1=1
AND E.EMPNO = #{empno}
AND E.EMP_ID = #{empId}
AND USE_PSBLTY_ACNT_YN = 'Y'

SELECT DISTINCT ea.ELCTRN_ATRZ_ID, ea.ELCTRN_ATRZ_TY_SE_CD, -- newReq ea.ATRZ_FORM_DOC_ID, -- newReq ea.PRJCT_ID, -- newReq ea.ELCTRN_ATRZ_DOC_NO, eadf.GNRL_ATRZ_TTL, -- 전자결재 유형 (SELECT EMP_FLNM FROM EMP WHERE EMP_ID = ea.ATRZ_DMND_EMP_ID) ATRZ_DMND_EMP_NM, -- 기안자 DATE_FORMAT(ea.REG_DT, '%Y-%m-%d %H:%i:%s') AS REG_DT, CASE WHEN ea.ATRZ_HIST_SE_CD = 'VTW05405' THEN CONCAT('▶취소결재◀ ', COALESCE(ga.GNRL_ATRZ_TTL, ca.CLM_ATRZ_TTL, cta.CTRT_ATRZ_TTL, cga.ATRZ_TTL, CONCAT((SELECT CD_NM FROM CD WHERE CD_VALUE = va.VCATN_TY_CD), ' [', DATE_FORMAT(va.VCATN_BGNG_YMD, '%Y-%m-%d'), ' ~ ', DATE_FORMAT(va.VCATN_END_YMD, '%Y-%m-%d'), ' (', va.VCATN_DE_CNT, '일)]'))) WHEN ea.ATRZ_HIST_SE_CD = 'VTW05406' THEN CONCAT('▶결재변경◀ ', COALESCE(ga.GNRL_ATRZ_TTL, ca.CLM_ATRZ_TTL, cta.CTRT_ATRZ_TTL, cga.ATRZ_TTL, CONCAT((SELECT CD_NM FROM CD WHERE CD_VALUE = va.VCATN_TY_CD), ' [', DATE_FORMAT(va.VCATN_BGNG_YMD, '%Y-%m-%d'), ' ~ ', DATE_FORMAT(va.VCATN_END_YMD, '%Y-%m-%d'), ' (', va.VCATN_DE_CNT, '일)]'))) ELSE COALESCE(ga.GNRL_ATRZ_TTL, ca.CLM_ATRZ_TTL, cta.CTRT_ATRZ_TTL, cga.ATRZ_TTL, CONCAT((SELECT CD_NM FROM CD WHERE CD_VALUE = va.VCATN_TY_CD), ' [', DATE_FORMAT(va.VCATN_BGNG_YMD, '%Y-%m-%d'), ' ~ ', DATE_FORMAT(va.VCATN_END_YMD, '%Y-%m-%d'), ' (', va.VCATN_DE_CNT, '일)]')) END AS TITLE, cad.CNPT_NM, (SELECT EMP_FLNM FROM EMP WHERE EMP_ID = al.APRVR_EMP_ID) APRVR_EMP_ID, -- 결재자 al.ATRZ_STEP_CD, -- 현재 결재단계 (SELECT CD_NM FROM CD WHERE CD_VALUE = al.ATRZ_STEP_CD) ATRZ_STEP_CD_NM, -- 현재 결재단계명 ea.ATRZ_DMND_STTS_CD, -- 결재상태 (SELECT CD_NM FROM CD WHERE CD_VALUE = al.ATRZ_STTS_CD) ATRZ_STTS_CD, -- 결재상태명 al.ATRZ_OPNN_CN -- 결재의견 FROM ELCTRN_ATRZ ea LEFT JOIN ATRZ_LN al ON ea.ELCTRN_ATRZ_ID = al.ELCTRN_ATRZ_ID -- 현재 결재단계만 LEFT JOIN REFRN_MAN rm ON ea.ELCTRN_ATRZ_ID = rm.ELCTRN_ATRZ_ID -- 참조자의 경우 LEFT JOIN GNRL_ATRZ ga ON ea.ELCTRN_ATRZ_ID = ga.ELCTRN_ATRZ_ID -- 일반결재 LEFT JOIN CLM_ATRZ ca ON ea.ELCTRN_ATRZ_ID = ca.ELCTRN_ATRZ_ID -- 청구결재 LEFT JOIN CLM_ATRZ_DTL cad ON ea.ELCTRN_ATRZ_ID = cad.ELCTRN_ATRZ_ID -- 청구결재 상세 LEFT JOIN CTRT_ATRZ cta ON ea.ELCTRN_ATRZ_ID = cta.ELCTRN_ATRZ_ID -- 계약결재 LEFT JOIN CTRT_GIVE_ATRZ cga ON ea.ELCTRN_ATRZ_ID = cga.ELCTRN_ATRZ_ID -- 계약지급결재 LEFT JOIN VCATN_ATRZ va ON ea.ELCTRN_ATRZ_ID = va.ELCTRN_ATRZ_ID -- 휴가결재 LEFT JOIN ELCTRN_ATRZ_DOC_FORM eadf ON ea.ATRZ_FORM_DOC_ID = eadf.ATRZ_FORM_DOC_ID -- 서식 제목 WHERE 1=1
AND (ATRZ_STTS_CD = 'VTW00801' OR ATRZ_DMND_STTS_CD = 'VTW03702')
AND (ATRZ_STTS_CD = 'VTW00802' OR ATRZ_DMND_STTS_CD = 'VTW03703')
AND (ATRZ_STTS_CD = 'VTW00803' OR ATRZ_DMND_STTS_CD = 'VTW03704')
AND PRJCT_ID = #{prjctId}
AND ELCTRN_ATRZ_TY_SE_CD = #{elctrnAtrzTySeCd}
AND ATRZ_DMND_EMP_NM LIKE CONCAT('%', LOWER(#{atrzDmndEmpNm}), '%')
AND CNPT_NM LIKE CONCAT('%', LOWER(#{cnptNm}), '%')
AND ( LOWER(ga.GNRL_ATRZ_TTL) LIKE CONCAT('%', LOWER(#{title}), '%') OR LOWER(ca.CLM_ATRZ_TTL) LIKE CONCAT('%', LOWER(#{title}), '%') OR LOWER(cga.ATRZ_TTL) LIKE CONCAT('%', LOWER(#{title}), '%') OR LOWER(va.VCATN_TY_CD) IN (SELECT CD_VALUE FROM CD WHERE LOWER(CD_NM) LIKE CONCAT('%', LOWER(#{title}), '%')) )
AND DATE_FORMAT(ea.REG_DT, '%Y%m%d') >= #{startDate}
AND DATE_FORMAT(ea.REG_DT, '%Y%m%d') <= #{endDate}
AND DATE_FORMAT(ea.REG_DT, '%Y%m%d') BETWEEN #{startDate} AND #{endDate}
ORDER BY REG_DT DESC
LIMIT 0, 20
LIMIT #{startVal}, #{pageSize}

mapper.update:
/* sysMngMapper.updateCrtrYmd */
UPDATE CRTR_DATE
SET
	HLDY_CL_CD				= #{hldyClCd}
	,HLDY_NM				= #{hldyNm}
	,PASSIV_INFO_INPT_YN 	= 'N'
WHERE CRTR_YMD = #{crtrYmd}
